mapper.select:
SELECT T.GROUP_ID_1_NAME,T.UNIT_NAME,T.HQ_CHAN_CODE,T.GROUP_ID_4_NAME,T3.CHANL_TYPE_NAME as CHNL_TYPE, TO_CHAR(T2.RATIO, 'fm9999999990.0000') AS RATIO, TO_CHAR(T2.SERVER_RATIO, 'fm9999999990.0000') AS SERVER_RATIO, DECODE(T1.STATUS,'00','草稿','01','待审核','10','正常','11','清算','12','终止','') AS STATUS FROM PCDE.TAB_CDE_CHANL_HQ_CODE T LEFT JOIN PCDE.TB_CDE_CHANL_HQ_CODE T1 ON (T.FD_CHNL_CODE = T1.FD_CHNL_CODE) LEFT JOIN PTEMP.ALL_CHL_RATIO_LIST T2 ON (T.FD_CHNL_CODE = T2.HQ_CHANL_CODE) LEFT JOIN PTEMP.TB_TEMP_JCDY_CHANL_TYPE T3 ON (T2.CHNL_TYPE = T3.CHANL_TYPE_CODE) WHERE T.IS_SIGN = 1 AND T2.DEAL_DATE=#{month}
AND T.GROUP_ID_1 = #{code}
AND T.UNIT_ID = #{code}
AND 1 = 2
AND T.HQ_CHAN_CODE = #{hq_chan_code}
AND T.GROUP_ID_4_NAME LIKE #{group_id_4_name}
AND T1.STATUS NOT IN ('00', '01', '10', '11', '12')
AND T1.STATUS = #{status}
AND T2.RATIO IS NOT NULL
AND T2.RATIO IS NULL
AND T2.SERVER_RATIO IS NOT NULL
AND T2.SERVER_RATIO IS NULL
ORDER BY T.GROUP_ID_1, T.UNIT_ID, T.GROUP_ID_4
