mapper.delete:
DELETE FROM
PORTAL.TAB_PORTAL_GRP_PERSON WHERE DEVELOPER =#{dev_num}
AND DEAL_DATE
= #{dealDate}

mapper.select:
SELECT * FROM( SELECT T.FD_CHNL_CODE, T1.UNIT_ID, T1.UNIT_NAME,T1.GROUP_ID_4,T1.GROUP_ID_1 FROM PCDE.TB_CDE_CHANL_CODE T LEFT JOIN PCDE.TAB_CDE_CHANL_HQ_CODE T1 ON (T.HQ_CHAN_CODE = T1.HQ_CHAN_CODE) WHERE T.IS_DEVELOPER = 1 and T.FD_CHNL_CODE is not null AND NOT EXISTS (SELECT 1 FROM PORTAL.TAB_PORTAL_GRP_PERSON WHERE DEVELOPER = T.FD_CHNL_CODE AND DEAL_DATE=#{dealDate}) AND T.GROUP_ID_1=16001 UNION ALL SELECT T.FD_CHNL_CODE, T1.UNIT_ID, T1.UNIT_NAME,T1.GROUP_ID_4,T1.GROUP_ID_1 FROM PCDE.TB_CDE_CHANL_CODE T LEFT JOIN PCDE.TAB_CDE_CHANL_HQ_CODE T1 ON (T.HQ_CHAN_CODE = T1.HQ_CHAN_CODE) WHERE T.IS_DEVELOPER = 1 and T.FD_CHNL_CODE is not null AND NOT EXISTS (SELECT 1 FROM PORTAL.TAB_PORTAL_GRP_PERSON WHERE DEVELOPER = T.FD_CHNL_CODE AND DEAL_DATE=#{dealDate}) AND EXISTS (SELECT 1 FROM PCDE.TB_CDE_CHANL_HQ_CODE T0 WHERE T0.HQ_CHAN_CODE=T.HQ_CHAN_CODE AND T0.CHN_CDE_1_NAME LIKE '%自有%' AND T0.GROUP_ID_1!=16001 ) ) WHERE 1=1
AND GROUP_ID_1=#{orgCode}
AND UNIT_ID=#{orgCode}
AND GROUP_ID_4=#{orgCode}
AND FD_CHNL_CODE=#{devNum}
AND UNIT_NAME LIKE #{unit_name}

SELECT CASE T.USER_TYPE WHEN '1' THEN '渠道经理' WHEN '2' THEN '客户经理' ELSE '其他' END USER_TYPE, T.NAME, T.PHONE, T.DEVELOPER, T1.BSS_CHNL_CODE, T.UNIT_NAME, T.HQ_CHAN_CODE, T.HQ_CHAN_NAME, T.ACTIVE_TIME FROM PORTAL.TAB_PORTAL_GRP_PERSON T JOIN PCDE.TB_CDE_CHANL_CODE T1 ON (T.DEVELOPER = T1.FD_CHNL_CODE) WHERE T.IS_VALUE = 1
AND T.GROUP_ID_1=#{groupId}
AND T.UNIT_ID=#{groupId}
AND T.USER_TYPE=#{type}
AND T.NAME like #{name}
AND T.PHONE like #{phone}
AND T.HQ_CHAN_CODE like #{chanCode}
AND T.DEAL_DATE = #{dealDate}
AND T.DEVELOPER = #{developer}
AND INSTR( T1.BSS_CHNL_CODE,#{bssCode})>0
ORDER BY T.HQ_CHAN_CODE
