select.if:
   		SELECT 
				  T2.DEAL_DATE,T1.GROUP_ID_1,T1.GROUP_ID_1_NAME,T1.UNIT_ID,T1.UNIT_NAME,T1.STD_6_ID,T1.STD_6_NAME,T2.NAME,T2.ACCOUNT,
				T2.PHONE,T2.HR_ID,CASE WHEN T3.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS ISDIVIDE,
      			CASE WHEN T2.USERID IS NOT NULL THEN 1 ELSE 0 END AS ISBIND,T4.ID
 			FROM PCDE.TAB_CDE_6_STD T1 LEFT JOIN PORTAL.TAB_PORTAL_NET_PERSON T2
   			ON (T1.STD_6_ID = T2.STD_ID_6)
 			LEFT JOIN PCDE.TAB_CDE_GROUP_CODE T3
   			ON (T1.UNIT_ID = T3.UNIT_ID)
   		LEFT JOIN PORTAL.APDP_ORG T4 
   			ON (T3.GROUP_ID_1=T4.CODE)
			WHERE T1.STATE = 'U' AND T1.IS_SIGN = 1
			
				AND T1.UNIT_NAME LIKE #{unit_name}
			
			
				AND T1.STD_6_ID = #{std_6_id}
			
			
				AND T1.STD_6_NAME LIKE #{std_6_name}
			
			
				AND T2.NAME LIKE #{name}
			
			
				AND T2.ACCOUNT = #{account}
			
			
				AND T2.HR_ID = #{hr_id}
			
			
				AND T2.DEAL_DATE = #{deal_date}
			
			ORDER BY T2.GROUP_ID_1, T2.UNIT_ID, T2.STD_ID_6

			SELECT 
 				  T2.DEAL_DATE,T1.GROUP_ID_1,T1.GROUP_ID_1_NAME,T1.UNIT_ID,T1.UNIT_NAME,T1.STD_6_ID,T1.STD_6_NAME,T2.NAME,T2.ACCOUNT,
 				T2.PHONE,T2.HR_ID,CASE WHEN T3.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS ISDIVIDE,
       			CASE WHEN T2.USERID IS NOT NULL THEN 1 ELSE 0 END AS ISBIND,T4.ID
  			FROM PCDE.TAB_CDE_6_STD T1 LEFT JOIN PORTAL.TAB_PORTAL_NET_PERSON T2
    			ON (T1.STD_6_ID = T2.STD_ID_6)
  			LEFT JOIN PCDE.TAB_CDE_GROUP_CODE T3
    			ON (T1.UNIT_ID = T3.UNIT_ID)
    		LEFT JOIN PORTAL.APDP_ORG T4 
    			ON (T3.GROUP_ID_1=T4.CODE)
 			WHERE T1.STATE = 'U' AND T1.IS_SIGN = 1
 			AND T1.GROUP_ID_1 = #{code}
 			
 				AND T1.UNIT_NAME LIKE #{unit_name}
 			
 			
 				AND T1.STD_6_ID = #{std_6_id}
 			
 			
 				AND T1.STD_6_NAME LIKE #{std_6_name}
 			
 			
 				AND T2.NAME LIKE #{name}
 			
 			
 				AND T2.ACCOUNT = #{account}
 			
 			
 				AND T2.HR_ID = #{hr_id}
 			
 			
 				AND T2.DEAL_DATE = #{deal_date}
 			
 			ORDER BY T2.GROUP_ID_1, T2.UNIT_ID, T2.STD_ID_6

			SELECT 
 				  T2.DEAL_DATE,T1.GROUP_ID_1,T1.GROUP_ID_1_NAME,T1.UNIT_ID,T1.UNIT_NAME,T1.STD_6_ID,T1.STD_6_NAME,T2.NAME,T2.ACCOUNT,
 				T2.PHONE,T2.HR_ID,CASE WHEN T3.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS ISDIVIDE,
       			CASE WHEN T2.USERID IS NOT NULL THEN 1 ELSE 0 END AS ISBIND,T4.ID
  			FROM PCDE.TAB_CDE_6_STD T1 LEFT JOIN PORTAL.TAB_PORTAL_NET_PERSON T2
    			ON (T1.STD_6_ID = T2.STD_ID_6)
  			LEFT JOIN PCDE.TAB_CDE_GROUP_CODE T3
    			ON (T1.UNIT_ID = T3.UNIT_ID)
    		LEFT JOIN PORTAL.APDP_ORG T4 
    			ON (T3.GROUP_ID_1=T4.CODE)
 			WHERE T1.STATE = 'U' AND T1.IS_SIGN = 1
 			AND T1.UNIT_ID = #{code}
 			
 				AND T1.UNIT_NAME LIKE #{unit_name}
 			
 			
 				AND T1.STD_6_ID = #{std_6_id}
 			
 			
 				AND T1.STD_6_NAME LIKE #{std_6_name}
 			
 			
 				AND T2.NAME LIKE #{name}
 			
 			
 				AND T2.ACCOUNT = #{account}
 			
 			
 				AND T2.HR_ID = #{hr_id}
 			
 			
 				AND T2.DEAL_DATE = #{deal_date}
 			
 			ORDER BY T2.GROUP_ID_1, T2.UNIT_ID, T2.STD_ID_6

mapper.select:
		
         SELECT 
       		T.ID, T.REALNAME, T.USERNAME, T.PHONE, T.HR_ID
			FROM PORTAL.APDP_USER T
		WHERE EXISTS (SELECT 1 FROM PORTAL.APDP_ORG T1 WHERE T1.REGION_CODE = #{group_id_1}
         	AND T.ORG_ID = T1.ID) AND NOT EXISTS (SELECT 1
       	FROM PORTAL.VIEW_U_PORTAL_PERSON t5 WHERE T.ID = T5.USERID 
       	AND t5.GROUP_ID_1 = #{group_id_1} AND t5.UNIT_ID != #{unit_id} AND T5.DEAL_DATE=#{deal_date}) AND T.ENABLED = 1
        
        		AND T.REALNAME LIKE #{name}
        
        
        		AND T.USERNAME=#{username}
