select.if:
SELECT T.GROUP_ID_1,T.GROUP_ID_1_NAME,T.UNIT_ID,T.UNIT_NAME,T.STD_6_ID, T.STD_6_NAME,CASE WHEN T2.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS IS_DIVIDE, T.HOUSE_PE,T.COVER_HOUSE_PE,T.JD,T.WD,T.ZW_NAME,T.ZW_NAME_NUM, T.SERVICE_NAME,T.SERVICE_NUM FROM PCDE.TAB_CDE_6_STD T,PCDE.TAB_CDE_GROUP_CODE T2 WHERE T.UNIT_ID = T2.UNIT_ID AND T.STATE = 'U' AND T.IS_SIGN = 1
AND T.STD_6_ID = #{std_6_id}
AND T.STD_6_NAME LIKE #{std_6_name}
ORDER BY T.GROUP_ID_1, T.UNIT_ID, T.STD_6_ID

SELECT T.GROUP_ID_1,T.GROUP_ID_1_NAME,T.UNIT_ID,T.UNIT_NAME,T.STD_6_ID, T.STD_6_NAME,CASE WHEN T2.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS IS_DIVIDE, T.HOUSE_PE,T.COVER_HOUSE_PE,T.JD,T.WD,T.ZW_NAME,T.ZW_NAME_NUM, T.SERVICE_NAME,T.SERVICE_NUM FROM PCDE.TAB_CDE_6_STD T,PCDE.TAB_CDE_GROUP_CODE T2 WHERE T.UNIT_ID = T2.UNIT_ID AND T.STATE = 'U' AND T.IS_SIGN = 1 AND T.GROUP_ID_1 = #{code}
AND T.STD_6_ID = #{std_6_id}
AND T.STD_6_NAME LIKE #{std_6_name}
ORDER BY T.GROUP_ID_1, T.UNIT_ID, T.STD_6_ID

SELECT T.GROUP_ID_1,T.GROUP_ID_1_NAME,T.UNIT_ID,T.UNIT_NAME,T.STD_6_ID, T.STD_6_NAME,CASE WHEN T2.IS_DEFAULT = 1 THEN 0 ELSE 1 END AS IS_DIVIDE, T.HOUSE_PE,T.COVER_HOUSE_PE,T.JD,T.WD,T.ZW_NAME,T.ZW_NAME_NUM, T.SERVICE_NAME,T.SERVICE_NUM FROM PCDE.TAB_CDE_6_STD T,PCDE.TAB_CDE_GROUP_CODE T2 WHERE T.UNIT_ID = T2.UNIT_ID AND T.STATE = 'U' AND T.IS_SIGN = 1 AND T.UNIT_ID = #{code}
AND T.STD_6_ID = #{std_6_id}
AND T.STD_6_NAME LIKE #{std_6_name}
ORDER BY T.GROUP_ID_1, T.UNIT_ID, T.STD_6_ID

mapper.update:
		UPDATE PCDE.TAB_CDE_6_STD T
   			SET T.HOUSE_PE = #{house_pe},T.COVER_HOUSE_PE = #{cover_house_pe},
       		T.JD = #{jd},T.WD = #{wd},T.ZW_NAME = #{zw_name},T.ZW_NAME_NUM = #{zw_name_num},
       		T.SERVICE_NAME = #{service_name},T.SERVICE_NUM = #{service_num} 
       	WHERE T.STD_6_ID = #{std_6_id}
