mapper.select:
    		SELECT 
			       T.CHANLID,
			       T.CHANLNAME,
			       T.GROUP_ID_1_NAME,
			       T.TOP_GROUP_ID_4_NAME,
			       T.AGENT_NAME,
			       T.AGENT_TEL,
			       T.UNIT_NAME,
			       T.STATE,
			       T.HR_ID
			FROM
			       PORTAL.TAB_PORTAL_NOTREATY_CHANL T 
	    	
	    		
					AND T.GROUP_ID_0=#{code}
				 
				
					AND T.GROUP_ID_1=#{code}
				
				
					AND T.UNIT_ID=#{code}
				
				
					AND 1=2
				
	    		
	    			AND T.STATE=#{status}
	    		  		
	    		
    				AND T.CHANLNAME LIKE #{chanlName}
		    	
		    	
		    		AND T.CHANLID LIKE #{chanlCode}
		    	
		    	
		    		AND T.UNIT_NAME LIKE #{unitName}
		    	
	    	
	    	ORDER BY T.GROUP_ID_1_NAME, T.UNIT_NAME,T.CHANLID

	SELECT
	      DISTINCT T.ID,T.REGION_CODE,T.REGION_NAME
	FROM
	      PORTAL.APDP_ORG T
	
	
    		
				AND T.PARENT_ID=#{orgId}
			 
		    
				AND T.ID=#{orgId}

SELECT DISTINCT T.ID,T.ORGNAME,T.CODE FROM PORTAL.APDP_ORG T
AND T.PARENT_ID=#{orgId}
AND T.ID=#{orgId}
AND 1=2

SELECT T.ID, T.REALNAME, T.USERNAME, T.PHONE, T.HR_ID FROM PORTAL.APDP_USER T WHERE EXISTS (SELECT 1 FROM PORTAL.APDP_ORG T1 WHERE T1.REGION_CODE = #{areaCode} AND T.ORG_ID = T1.ID) AND NOT EXISTS (SELECT 1 FROM PORTAL.VIEW_U_PORTAL_PERSON T5 WHERE T.ID = T5.USERID AND T5.GROUP_ID_1 = #{areaCode} AND T5.UNIT_ID <> #{unitId}) AND T.ENABLED = 1
AND T.REALNAME LIKE #{name}
AND T.USERNAME LIKE #{username}

mapper.update:
UPDATE PORTAL.TAB_PORTAL_NOTREATY_CHANL
	SET STATE=#{state}
WHERE 
	CHANLID=#{CHANLID}

mapper.select:
        SELECT * FROM PORTAL.TB_PORTAL_PICTURE T 
		WHERE T.HQ_CHNL_CODE =#{CHANLID}
  		AND T.CHNL_TYPE=2
  		AND T.PIC_TYPE=#{PIC_TYPE}
