mapper.select:
SELECT DEAL_DATE, GROUP_ID_1_NAME, UNIT_NAME, HR_ID, HR_ID_NAME, FD_CHNL_ID, GROUP_ID_4_NAME, DEPT_TYPE, HZ_MONTH, INTEGRAL_GRADE, nvl(ALL_JF_TOTAL, 0) ALL_JF_TOTAL, nvl(ALL_JF_QS, 0) ALL_JF_QS, NVL(ALL_JF_TOTAL, 0) + NVL(ALL_JF_QS, 0) MON_JF, DECODE(INTEGRAL_GRADE, 'D', NULL, NVL(COMM, 0)) MON_JE, NVL(LJ_JF_TOTAL, 0) LJ_JF_TOTAL, NVL(LJ_JF_QS, 0) LJ_JF_QS, NVL(LJ_JF_DH, 0) LJ_JF_DH, DECODE(INTEGRAL_GRADE, 'D', NULL, NVL(LJ_COMM, 0)) LJ_BN_JE, IS_JF, IS_COMM IS_JF_JE, IS_JF_LJ_ALL IS_JF_LJ_ALL, IS_COMM_LJ_ALL IS_COMM_LJ_ALL, IS_JF_SPLUS_ALL IS_JF_SPLUS_ALL, STATUS, UP_JF FROM PMRT.TAB_MRT_INTEGRAL_DEV_REPORT T WHERE T.INTEGRAL_SUB = 1 AND T.INIT_ID = #{businessKey}
AND T.FD_CHNL_ID= #{channelCode}
ORDER BY GROUP_ID_4_NAME,FD_CHNL_ID ASC

mapper.update:
		UPDATE PMRT.TAB_MRT_INTEGRAL_DEV_REPORT T
   			SET T.INIT_ID = #{workNo},T.STATUS=#{status}
 		WHERE T.DEAL_DATE = #{dealDate}
   		AND T.GROUP_ID_1 = #{code}
   		AND T.INTEGRAL_SUB = 1
