mapper.select:
SELECT DISTINCT T.DEAL_DATE, T.GROUP_ID_1_NAME, T.UNIT_NAME, T.INIT_ID, T.UNIT_ID, TO_CHAR(T1.END_TIME_, 'yyyy-MM-dd HH24:mi:ss') AS END_TIME_, T2.REALNAME, DECODE(T.ISCONFIM, '1', '已确认', 2,'已拒绝','未确认') AS ISCONFIM_CN, T.ISCONFIM IS_CONFIRM_EN FROM PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T,PORTAL.ACT_HI_PROCINST T1, PORTAL.APDP_USER T2 WHERE T.INIT_ID = T1.BUSINESS_KEY_ AND T1.START_USER_ID_ = T2.ID AND T.STATUS = '10'
AND T.GROUP_ID_1 = #{code}
AND T.UNIT_ID = #{code}
AND 1=2
AND T.DEAL_DATE = #{deal_date}
AND T.UNIT_NAME LIKE #{unit_name}
AND T.ISCONFIM = '1'
AND T.ISCONFIM IS NULL
AND T.GROUP_ID_1 = #{group_id_1}
ORDER BY T.DEAL_DATE DESC, T.UNIT_ID ASC

mapper.update:
		UPDATE PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T SET T.ISCONFIM = '1',
		T.CONFIRM_USER=#{confirm_user},T.CONFIRM_DATE=sysdate
 		WHERE T.DEAL_DATE = #{deal_date} AND T.INIT_ID = #{init_id} AND T.UNIT_ID = #{unit_id}

		UPDATE PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T SET T.ISCONFIM = '2',
    	T.CONFIRM_USER=#{confirm_user},T.CONFIRM_DATE=sysdate,T.REMARK=#{refuseReason}
     	WHERE T.DEAL_DATE = #{deal_date} AND T.INIT_ID = #{init_id} AND T.UNIT_ID = #{unit_id}
