mapper.select:
SELECT T.DEAL_DATE,T.GROUP_ID_1_NAME,T.UNIT_NAME,T.COST_CENTER_CODE, T.COST_CENTER_NAME,T.UNIT_ITEM,T.BUDGET_ITEM_CODE,T.BUDGET_ITEM_NAME, TO_CHAR(T.BUDGET_MONEY, 'fm9999999999990.00') AS BUDGET_MONEY, TO_CHAR(T.ZSB_RATE, 'fm99999999990.00') AS ZSB_RATE, DECODE(T.FLAG, '1', '占收比', '2', '定额', '') AS FLAG,T.ID,T.FLAG AS F, DECODE(T.STATUS, '10', '已下发', '未下发') AS STATUS, DECODE(T.ISCONFIM, '2', '已拒绝', '1', '已确认', '未确认') AS ISCONFIM, ISCONFIM as ISCONFIM_EN FROM PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T WHERE (T.STATUS IS NULL OR STATUS = '11' OR T.ISCONFIM = 2)
AND T.AREA_NAME = #{code}
AND T.UNIT_ID = #{code}
AND 1=2
AND T.COST_CENTER_NAME LIKE #{cost_center_name}
AND T.deal_date = #{deal_date}

mapper.update:
UPDATE PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T
   SET T.ZSB_RATE = #{zsb_rate},T.BUDGET_MONEY = #{budget_money}
 WHERE T.ID = #{id}

		UPDATE PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T
   			SET T.INIT_ID = #{workNo},T.STATUS='9',
   			ISCONFIM='',CONFIRM_USER='',CONFIRM_DATE='',REMARK=''
 		WHERE T.DEAL_DATE = #{deal_date}
   		AND T.AREA_NAME = #{code}

mapper.select:
SELECT T.DEAL_DATE, T.GROUP_ID_1_NAME, T.UNIT_NAME, T.COST_CENTER_CODE, T.COST_CENTER_NAME, T.UNIT_ITEM, T.BUDGET_ITEM_CODE, T.BUDGET_ITEM_NAME, TO_CHAR(T.BUDGET_MONEY, 'fm9999999999990.00') AS BUDGET_MONEY, TO_CHAR(T.ZSB_RATE, 'fm99999999990.00') AS ZSB_RATE, DECODE(T.FLAG, '1', '占收比', '2', '定额', '') AS FLAG, T.ID, T.FLAG AS F FROM PORTAL.TAB_PORTAL_COST_BUDGETTOERP_IM T WHERE T.INIT_ID = #{businessKey}
AND T.DEAL_DATE = #{deal_date}
AND T.COST_CENTER_NAME LIKE #{cost_center_name}
