mapper.select:
SELECT SUBSTR(T.DEAL_DATE, 7) || '日' AS X, SUM(T.DEV_2G_NUM) AS Y1, SUM(T.DEV_3G_NUM) + SUM(T.DEV_WIFI_NUM) AS Y2, SUM(T.DEV_4G_NUM) AS Y3 FROM PMRT.TAB_MRT_INCOME_DEV_CHNL_DAY T WHERE T.DEAL_DATE BETWEEN TO_CHAR(TRUNC(SYSDATE-1, 'mm'), 'yyyyMMdd') AND TO_CHAR(LAST_DAY(SYSDATE-1), 'yyyyMMdd')
AND T.GROUP_ID_1 = #{code}
AND T.UNIT_ID = #{code}
AND T.GROUP_ID_4 = #{code}
GROUP BY T.DEAL_DATE ORDER BY T.DEAL_DATE

SELECT SUBSTR(T.DEAL_DATE, 7) || '日' AS X, SUM(T.SR_2G_NUM) AS Y1, SUM(T.SR_3G_NUM) + SUM(T.SR_WIFI_NUM) AS Y2, SUM(T.SR_4G_NUM) AS Y3 FROM PMRT.TAB_MRT_INCOME_DEV_CHNL_DAY T WHERE T.DEAL_DATE BETWEEN TO_CHAR(TRUNC(SYSDATE-1, 'mm'), 'yyyyMMdd') AND TO_CHAR(LAST_DAY(SYSDATE-1), 'yyyyMMdd')
AND T.GROUP_ID_1 = #{code}
AND T.UNIT_ID = #{code}
AND T.GROUP_ID_4 = #{code}
GROUP BY T.DEAL_DATE ORDER BY T.DEAL_DATE

select.if:
		SELECT 
		      T.LONGITUDE LOG_NO,
          	  T.LATITUDE LAT_NO,
		      T.STATION_TYPE_CODE,
		      T.STATION_NAME
		FROM PCDE.TAB_CDE_STATION_NE_ADDR_CODE T
	    WHERE 1=1
		
			AND T.LATITUDE BETWEEN #{lat0} AND #{lat1}
        	AND T.LONGITUDE BETWEEN #{log0} AND #{log1}
	        AND T.STATION_TYPE_CODE ${status}

mapper.select:
		select rownum,t.* from
		(SELECT *
  			FROM PMRT.TB_MRT_JCDY_DEV_RANK_MON t
 		 WHERE t.deal_date=#{dealDate}
 		
			and t.GROUP_ID_1=#{code}
		
		
			and t.UNIT_ID=#{code}
		
		
			and t.HR_NO=#{hrId}
		
		order by RANK asc) t
		where rownum<21

SELECT * 
	FROM portal.tab_portal_bulletin T
WHERE T.BULLETINID=#{id}

		SELECT *
   			FROM (SELECT T.ID, T.OLD_NAME oldName FROM PORTAL.TAB_PORTAL_DOC T ORDER BY T.CREATE_DATE DESC)
  		WHERE ROWNUM <= #{num}

SELECT  COUNT(*) FROM PCDE.TAB_CDE_CHANL_HQ_CODE T,PCDE.TAB_CDE_GROUP_CODE T1 
	WHERE T.UNIT_ID = T1.UNIT_ID AND T1.IS_DEFAULT =1 AND T.IS_SIGN = 1 

	AND T.GROUP_ID_1 = #{orgCode}


	AND T.UNIT_ID = #{orgCode}
