mapper.select:
SELECT L.APPNAME, O.REGION_NAME, O.REGION_CODE, O.ORGNAME, U.REALNAME , U.USERNAME, U.PHONE, L.TIMES, L.LOGINTIME FROM (SELECT T.APPNAME, T.USERNAME,TO_CHAR(T.LOGINTIME, 'yyyymmdd') AS LOGINTIME, COUNT(T.USERNAME) AS TIMES FROM PORTAL.APDP_LOG_USERLOGIN T WHERE TO_CHAR(T.LOGINTIME, 'yyyymmdd') BETWEEN #{startTime} AND #{endTime} AND T.APPNAME = #{appName} GROUP BY T.APPNAME, T.USERNAME,T.LOGINTIME) L, PORTAL.APDP_USER U, PORTAL.APDP_ORG O WHERE UPPER(U.USERNAME) = UPPER(L.USERNAME) AND U.ORG_ID=O.ID
AND O.REGION_CODE = #{queryOrgCode}
AND U.USERNAME = #{username}
AND U.REALNAME LIKE #{realname}
