mapper.select:
SELECT L.APPNAME, O.REGION_NAME AS ORGNAME, O.REGION_CODE, U.REALNAME , U.USERNAME, U.PHONE, L.TIMES FROM (SELECT T.APPNAME, T.USERNAME, COUNT(T.USERNAME) AS TIMES FROM PORTAL.APDP_LOG_USERLOGIN T WHERE TO_CHAR(T.LOGINTIME, 'yyyymmdd') BETWEEN #{startTime} AND #{endTime} AND T.APPNAME = #{appName} GROUP BY T.APPNAME, T.USERNAME) L, PORTAL.APDP_USER U, PORTAL.APDP_ORG O WHERE UPPER(U.USERNAME) = UPPER(L.USERNAME) AND U.ORG_ID=O.ID
AND O.region_code = #{queryOrgCode}
AND U.USERNAME = #{username}
AND U.REALNAME LIKE #{realname}
