mapper.select:
SELECT DEAL_DATE, GROUP_ID_1_NAME, GROUP_ID_1, BD_TYPE_ID, BD_TYPE, SUM(INIT_NUM) INI_NUM, SUM(INIT_FEE) INIT_FEE, SUM(SUCCESS_NUM) SUCESS_NUM, sum(SUCCESS_FEE) SUCCESS_FEE, SUM(FAIL_NUM) FAIL_NUM, SUM(FAIL_FEE_YL) FAIL_FEE_YL, SUM(FAIL_FEE_SL) FAIL_FEE_SL, SUM(FAIL_FEE_XY) FAIL_FEE_XY FROM PMRT.TAB_MRT_COMM_BD_DATA_DETAIL T WHERE DEAL_DATE = #{dealDate}
AND T.GROUP_ID_1= #{regionCode}
AND T.GROUP_ID_1 = #{region}
GROUP BY DEAL_DATE,GROUP_ID_1, GROUP_ID_1_NAME, BD_TYPE_ID, BD_TYPE
