mapper.select:
SELECT COUNT(*) AS TOTALCOUNT FROM PMRT.TAB_MRT_COMM_01_HH WHERE DEAL_DATE=TO_CHAR(ADD_MONTHS(sysdate,-1), 'yyyymm') AND INIT_ID IS NULL AND GROUP_ID_1 = #{code}
AND REMARK LIKE CONCAT(CONCAT('%', #{remark}), '%')
GROUP BY DEAL_DATE,CHANNEL_NAME,CHANNEL_ID

mapper.update:
		UPDATE PMRT.TAB_MRT_COMM_01_HH 
   			SET INIT_ID = #{workNo}
 		WHERE GROUP_ID_1 = #{code} 
   		AND DEAL_DATE=to_char(add_months(sysdate,-1), 'yyyymm') AND INIT_ID IS NULL

mapper.select:
SELECT DEAL_DATE, CHANNEL_NAME, CHANNEL_ID, FD_CHNL_CODE, NVL(BAK_1, RULE_NAME) RULE_NAME, SUM(COMM) AS COMM, SUM(MOD_COMM) AS MOD_COMM, COMM_SUB FROM PMRT.TAB_MRT_COMM_01_HH WHERE INIT_ID = #{businessKey}
AND REMARK LIKE CONCAT(CONCAT('%', #{remark}), '%')
GROUP BY DEAL_DATE,CHANNEL_NAME,CHANNEL_ID,FD_CHNL_CODE, NVL(BAK_1,RULE_NAME),COMM_SUB ORDER BY COMM_SUB
