mapper.select:
SELECT NVL(SUM(FEE),0) FEE FROM PMRT.TAB_MRT_COMM_2G_AUDIT WHERE BILLINGCYCLID=to_char(add_months(sysdate,-1), 'yyyymm') AND INIT_ID IS NULL
AND 1=2
AND GROUP_ID_1 = #{code}

mapper.update:
		UPDATE PMRT.TAB_MRT_COMM_2G_AUDIT 
   			SET INIT_ID = #{workNo}
 		WHERE GROUP_ID_1 = #{code} 
   		AND BILLINGCYCLID=to_char(add_months(sysdate,-1), 'yyyymm') AND INIT_ID IS NULL
