mapper.select:
SELECT BILL_ID, BILLINGCYCLID, CHANNEL_NAME, AGENTID, DEPT_PTYPE, COMM_TYPE, SVCNUM, SUBJECTID, SVCTP, FEE, TOTALFEE, NETFEE, REMARK, PAY_ADDRESS FROM PAPP.TAB_COMM_IMPORT WHERE INIT_ID IS NULL AND ACCOUNT_ID=#{username}
AND 1=2
AND GROUP_ID = #{code}

SELECT NVL(SUM(FEE),0) FEE FROM PAPP.TAB_COMM_IMPORT WHERE INIT_ID IS NULL AND ACCOUNT_ID=#{username}
AND 1=2
AND GROUP_ID = #{code}

mapper.insert:
		INSERT INTO PAPP.TAB_COMM_IMPORT
  		SELECT * FROM PAPP.TAB_COMM_IMPORT_TEMP T1
    	WHERE ACCOUNT_ID=#{username} AND GROUP_ID=#{code}

mapper.delete:
DELETE FROM PAPP.TAB_COMM_IMPORT_TEMP WHERE 
ACCOUNT_ID=#{username}
AND GROUP_ID=#{code}

mapper.update:
		UPDATE PAPP.TAB_COMM_IMPORT 
   			SET INIT_ID = #{workNo}
 		WHERE GROUP_ID = #{code} AND ACCOUNT_ID=#{username}
   		AND INIT_ID IS NULL

mapper.select:
   SELECT * FROM PAPP.TAB_COMM_IMPORT_TEMP WHERE AGENTID NOT IN (SELECT A.BSS_CHNL_CODE FROM PCDE.TB_CDE_CHANL_CODE A)
     AND ACCOUNT_ID=#{username}
	AND GROUP_ID=#{code}
