mapper.select:
SELECT T.INSPEC_ID,T.INSPEC_NAME,T.C_USER_ID,T.START_TIME,T.END_TIME,T.C_USER_NAME, T.INSPEC_TYPE,T.IS_DEL,T.INSPEC_TYPE_NAME FROM ( SELECT T.INSPEC_ID, T.INSPEC_NAME, T.C_USER_ID, T.START_TIME, T.END_TIME, T.C_USER_NAME, T.INSPEC_TYPE,
CASE WHEN T.INSPEC_TYPE = '1' THEN '日常巡检' WHEN T.INSPEC_TYPE = '2' THEN '活动巡检' ELSE '' END AS INSPEC_TYPE_NAME,
CASE WHEN TO_CHAR(SYSDATE, 'yyyy-MM-dd') > T.END_TIME THEN '1' ELSE '0' END AS IS_DEL FROM PORTAL.TAB_PORTAL_INSPEC_TASK T WHERE T.IS_VALID = '1'
AND T.C_USER_REGION=#{code}
AND T.INSPEC_NAME LIKE #{inspec_name}
AND T.C_USER_NAME LIKE #{creator}
AND T.START_TIME >= #{startTime}
AND T.END_TIME >= #{endTime}
AND T.INSPEC_TYPE = #{inspec_type}
) T GROUP BY T.INSPEC_ID, T.INSPEC_NAME, T.C_USER_ID, T.START_TIME, T.END_TIME, T.C_USER_NAME, T.INSPEC_TYPE, T.IS_DEL, T.INSPEC_TYPE_NAME ORDER BY T.START_TIME DESC

SELECT T1.USERID, T1.NAME, T2.USERNAME, T2.PHONE,T2.ORG_ID,T3.CODE,T3.ORGLEVEL FROM PORTAL.VIEW_U_PORTAL_PERSON T1 
JOIN PORTAL.APDP_USER T2 
ON T1.USERID = T2.ID 
JOIN  PORTAL.APDP_ORG T3
ON T2.ORG_ID=T3.ID
WHERE T1.DEAL_DATE=#{month} AND T1.UNIT_ID = #{unit_id} AND T1.USER_CODE = '2' 

	AND T1.NAME LIKE #{realname}


	AND T2.USERNAME = #{username}


	AND T2.PHONE = #{phone}

SELECT T1.HQ_CHAN_NAME,T1.HQ_CHAN_CODE,T1.NAME,T1.PHONE, 1 AS CHLTYPE FROM PORTAL.TAB_PORTAL_MOB_PERSON T1 WHERE T1.DEAL_DATE=#{month} AND T1.USERID=#{userId}

	AND T1.HQ_CHAN_NAME LIKE #{group_id_4_name}


	AND T1.HQ_CHAN_CODE = #{hq_chanl_code}

		SELECT 
			T.ID, T.REALNAME, T.PHONE, T.USERNAME, T1.ORGLEVEL, T1.CODE
  		FROM PORTAL.APDP_USER T, PORTAL.APDP_ORG T1
 		WHERE T.ORG_ID = T1.ID AND T.ENABLED = 1 AND T.USERNAME IS NOT NULL
		
			AND T1.REGION_CODE = #{code}
		
		
			AND T1.CODE = #{code}
		
		
			AND T1.CODE = #{code}
		
		
			AND T.REALNAME LIKE #{realname}
		
		
			AND T.USERNAME = #{username}
		
		
			AND T.PHONE = #{phone}

SELECT 
	T.HQ_CHAN_CODE AS CODE, T.GROUP_ID_4_NAME AS NAME, 1 AS CHLTYPE
FROM PCDE.TAB_CDE_CHANL_HQ_CODE T WHERE T.STATUS = '10'

	AND T.GROUP_ID_1 = #{p_code}


	AND T.UNIT_ID = #{p_code}


	AND T.GROUP_ID_4 = #{p_code}


	AND T.GROUP_ID_4_NAME LIKE #{group_id_4_name}


	AND T.HQ_CHAN_CODE = #{hq_chanl_code}

UNION
SELECT 
	T.CHANLID AS CODE, T.CHANLNAME AS NAME, 2 AS CHLTYPE
FROM PORTAL.TAB_PORTAL_NOTREATY_CHANL T WHERE T.STATE = '1'

	AND T.GROUP_ID_1 = #{p_code}


	AND T.UNIT_ID = #{p_code}


	AND T.GROUP_ID_4 = #{p_code}


	AND T.CHANLNAME LIKE #{group_id_4_name}


	AND T.CHANLID = #{hq_chanl_code}
