$(function(){
	$("#dealDate").val(getMaxDate("PMRT.TB_MRT_BUS_DEVICE_SALE_MON"));
	var title=[["组织架构","经营模式","厅分类","模式一","","","","","","","是否割接","模式三","","","","","","","众筹终端","","一厅一商","","合计","","","",""],
	           ["","","","进货量","环比","销售量","环比","库存量","环比","库存周期","","进货量","环比","销售量","环比","库存量","环比","库存周期","销售量","环比","销售量","环比","销售量","环比","同比","定比去年12月","定比上季度月均"]];
	var field=["ROW_NAME","OPERATE_TYPE","CHNL_TYPE","JH_NUM1","JH_SEQUE","SALE_NUM1","SALE_SEQUE","KC_NUM1","KC_SEQUE","KC_CYCLE","FLAG","JH_NUM3","JH_SEQUE1","SALE_NUM3","SALE_SEQUE1","KC_NUM3","KC_SEQUE1","KC_CYCLE1","ZCZD_NUM","ZCZD_SEQUE","YTYS_NUM","YTYS_SEQUE","SALE_NUMALL","SALEALL_SEQUE","SALEALL_TB","SALEALL_DB","SALEALL_DB1"];
    $("#searchBtn").click(function(){
		//$("#searchForm").find("TABLE").find("TR:eq(0)").find("TD:last").remove();
		report.showSubRow();
        ///////////////////////////////////////////
		$("#lch_DataHead").find("TH").unbind();
		$("#lch_DataHead").find(".sub_on,.sub_off,.space").remove();
		///////////////////////////////////////////
	});
    var report=new LchReport({
		title:title,
		field:field,
		css:[{gt:0,css:LchReport.RIGHT_ALIGN}],
		rowParams:["ROW_ID"],//第一个为rowId
		content:"content",
		orderCallBack:function(index,type){
			
		},afterShowSubRows:function(){
			
		},
		getSubRowsCallBack:function($tr){
			var sql='';
			var code='';
			var orgLevel='';
			var where1="";
			var where2="";
			var where3="";
			var level;
			if($tr){
				code=$tr.attr("row_id");
				orgLevel=parseInt($tr.attr("orgLevel"));
				if(orgLevel==2){//点击省
					level=2;
				}else if(orgLevel==3||orgLevel==4){//点击市
					where1+=" AND GROUP_ID_1='"+code+"'";
					where2+=" AND T.GROUP_ID_1='"+code+"'";
					where3+=" AND GROUP_ID_1='"+code+"'";
					level=3;
				}else{
					return {data:[],extra:{}}
				}
				orgLevel++;
			}else{
				//先根据用户信息得到前几个字段
				code=$("#region").val();
				orgLevel=$("#orgLevel").val();
				if(orgLevel==1){//省
					level=1;
				}else if(orgLevel==2||orgLevel==3){//市
					where1+=" AND GROUP_ID_1='"+code+"'";
					where2+=" AND T.GROUP_ID_1='"+code+"'";
					where3+=" AND GROUP_ID_1='"+code+"'";
					level=2;
				}else{
					return {data:[],extra:{}};
				}
				orgLevel++;
			}
			sql=getSql(level,where1,where2,where3);
			var d=query(sql);
			return {data:d,extra:{orgLevel:orgLevel}};
		}
	});
	report.showSubRow();
	///////////////////////////////////////////
	$("#lch_DataHead").find("TH").unbind();
	$("#lch_DataHead").find(".sub_on,.sub_off,.space").remove();
	///////////////////////////////////////////
});

