SELECT T.USERID, T.NAME || '(' || T.USER_TYPE || ')' AS NAME, T.HR_ID, T.USER_CODE FROM PORTAL.VIEW_U_PORTAL_PERSON T WHERE T.DEAL_DATE=#{month} AND T.UNIT_ID = #{task_region_code} AND T.USER_CODE IN
#{item}
UNION ALL SELECT T.USERID, T.NAME || '(' || T.USER_TYPE || ')' AS NAME, T.HR_ID, T.USER_CODE FROM PORTAL.VIEW_U_PORTAL_PERSON T WHERE T.DEAL_DATE=#{month} AND T.UNIT_ID = #{task_region_code} AND T.USER_CODE = 3 AND EXISTS (SELECT 1 FROM PORTAL.TAB_PORTAL_GRP_PERSON T2 WHERE T.HR_ID = T2.HR_ID AND T2.F_HR_ID IS NULL AND T2.IS_VALID = 1 AND T2.USER_TYPE = 2 AND T2.DEAL_DATE=#{month})

select.if:
			SELECT 
				DISTINCT T1.NAME || '(' || T1.HR_ID || ')' AS NAME, T1.USERID, '4' AS USER_CODE
  			FROM PORTAL.TAB_PORTAL_MAG_PERSON T, PORTAL.TAB_PORTAL_MAG_PERSON T1
 			WHERE T.HR_ID = T1.F_HR_ID AND T.USER_TYPE = '1' AND T.USERID = #{task_region_code}
   			AND T.IS_VALID = '1' AND T1.IS_VALID = '1' AND T.DEAL_DATE=#{month} AND T1.DEAL_DATE=#{month}
